Rules.InvariantScript:
Select(Parent);
Set(
    selectedEmployee,
    ThisItem
);
Reset(TextInput9);
Collect(
    recentSearches,
    ThisItem.Worker
);
Set(
    selectedDataverseEmployee,
    LookUp(
        Employees,
        EmpID = selectedEmployee.Employee_ID
    )
);
Reset('Notes V2_1');
Concurrent(
    ClearCollect(
        compData,
        WorkdayGetCompHistory.Run(selectedEmployee.Employee_ID_s_)
    ),
    ClearCollect(
        jobDetails,
        First(WorkdayGetJobDetails.Run(selectedEmployee.Employee_ID_s_).Report_Entry)
    ),
    Set(
        JobDev,
        First(WorkdayGetJobDevDetails.Run(selectedEmployee.Employee_ID_s_).Report_Entry)
    ),
    ClearCollect(
        payByYear,
        WorkdayGetPaybyYear.Run(selectedEmployee.Employee_ID_s_)
    ),
    ClearCollect(
        teamInfo,
        WorkdayGetDirects.Run(
            LookUp(
                workerInfo,
                Employee_ID = selectedEmployee.Manager_ID,
                Employee_ID_s_
            )
        )
    ),
    ClearCollect(
        directInfo,
        WorkdayGetDirects.Run(selectedEmployee.Employee_ID_s_)
    ),
    ClearCollect(
        workerHistory,
        AddColumns(
            WorkdayGetWorkerHistory.Run(selectedEmployee.Employee_ID_s_).data,
            "Category",
            RandBetween(
                1,
                6
            )
        )
    ),
    Set(
        ReviewDetails,
        First(WorkdayGetReviewHistoryDetails.Run(selectedEmployee.Employee_ID_s_).Report_Entry)
    )
);
ClearCollect(
    workerHistoryTimeline,
    AddColumns(
        Filter(
            workerHistory,
            !IsBlank(effective)
        ),
        "Year",
        Year(effective)
    )
);
ClearCollect(
    timeLineData,
    AddColumns(
        GroupBy(
            workerHistoryTimeline,
            "Year",
            "Event Year"
        ),
        "expandCollapse",
        true
    )
);

Patch(
    timeLineData,
    First(timeLineData),
    {expandCollapse: true}
);
ClearCollect(
    notesTemp,
    AddColumns(
        Filter(
            selectedDataverseEmployee.'Employee Notes',
            !IsBlank(Note)
        ),
        "expandCollapse",
        false
    )
);
Clear(cat);
Navigate('Profile Screen');

Set(
    searchString,
    TextInput9.Text
);
Collect(
    recentSearches,
    TextInput9.Text
);
Navigate('Search Results');
Reset(TextInput9)
